import React, { useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Nav from '../components/nav';
import perfilPhoto from '../images/perfil.jpeg';
import '../css/home.css';

function Home() {
  const whreAmI = () => {
    setHomepage(true);
    const homeLink = document.getElementsByClassName('homeLink')[0];
    const aboutLink = document.getElementsByClassName('aboutLink')[0];
    const projectsLink = document.getElementsByClassName('projectsLink')[0];
    const contactLink = document.getElementsByClassName('contactLink')[0];
    homeLink.classList.add('selected');
    aboutLink.classList.remove('selected');
    projectsLink.classList.remove('selected');
    contactLink.classList.remove('selected');
  };

  useEffect(() => {
    whreAmI();
  }, []);

  return (
    <div className="home">
      <Nav />
      <img
        className="homePerfil"
        src={ perfilPhoto }
        alt="Foto de perfil de Tarcísio"
      />
      <h1>
        Tarcísio Menezes
      </h1>
      <hr />
      <p className="paragraphHome">
        Desenvolvedor Back-end com experiência em Node JS, MongoDB, MySQL,
        JavaScript, React, HTML, Jest, CSS dentre outras tecnologias.
      </p>
      <Link to="/sobre">
        <Button
          variant="outline-secondary"
        >
          Conheça mais
        </Button>
      </Link>
    </div>
  );
}

export default Home;
